mapper.select:
select gNo, gName, gIntro, uNick, memCnt, iCont, gThumbRen from ( select g.gNo, g.gName, g.gIntro, g.gThumbRen, i.iCont, u.uNick, memCnt, rownum as rNum from myGroup mg, moim g, tbl_user u, interest i, ( select gNo, count(*) as memCnt from myGroup group by gNo ) cnt where g.gNo = mg.gNo and g.iNo = i.iNo and mg.uNo = u.uNo and lNo = 1 and cnt.gNo = g.gNo and g.gNo not in ( select gNo from myGroup where uNo = #{uNo} ) and g.aNo in ( select aNo from area where gugun = ( select a.gugun from area a, tbl_user u where u.aNo=a.aNo and u.uNo=#{uNo} ) )
and g.iNo in ( select iNo from user_interest where uNo = #{uNo} )
and i.iNo = #{iNo}
and g.gName like '%'||#{keyword}||'%'
order by g.gCnt desc ) where rNum between #{start} and #{end}

mapper.delete:
delete from
	myGroup
where
	gNo = #{gNo}
	and uNo = #{uNo}

mapper.select:
select
	gNo
from 
	moim
where
	aNo = #{aNo}
	and iNo = #{iNo}
	and gThumbOri like #{gThumbOri}
	and gThumbRen like #{gThumbRen}
	and gCoverOri like #{gCoverOri}
	and gCoverRen like #{gCoverRen}
	and gName = #{gName}
	and gShort = #{gIntro}
	and gIntro like #{gShort}

mapper.update:
update
	moim
set
	gCnt = #{gCnt}
where
	gNo = #{gNo}

mapper.delete:
